    (6,3,'Joe Bobs','weaver','2006-12-18',5100,'','2','couldnt catch it in glass at this level very well...',0,'2019-09-10 12:03:55 -0400', '2019-09-10 12:03:55 -0400'),
    (5,3,'Bender','weaver','2007-04-30',4100,'','0','too much current...no eddy service..',0,'2019-09-10 12:03:55 -0400', '2019-09-10 12:03:55 -0400'),
